select ROWNUM num,r_num,r_guide,r_day,r_time,r_findDoc, r_regdate, m_id from(
SELECT * FROM IG_reserv where r_guide LIKE CONCAT('%', CONCAT(#{r_content}, '%')) order by r_num desc)
)
where num between #{begin}  and  #{end}

